            void equilatero()
            {
                double lado, resultado;
                Console.WriteLine("A elegido Equilatero porvavor proporcione el valor de un lado....");
                lado = double.Parse(Console.ReadLine());

                resultado = 3 * lado;
                Console.WriteLine("El perimetro del quilatero es de: "+resultado);

            }

            void isoseles()
            {
                double lado, resultado, baset;
                Console.WriteLine("A elegido isoseles porvavor proporcione el valor de un lado....");
                lado = double.Parse(Console.ReadLine());
                Console.WriteLine("Ingrese la base del triangulo...");
                baset = double.Parse(Console.ReadLine());

                resultado = (2*lado)+baset;
                Console.WriteLine("El perimetro del isoseles es de: " + resultado);
            }

            void escaleno()
            {
                double lado, resultado, lado2;
                Console.WriteLine("A elegido escaleno porvavor proporcione el valor del lado 1....");
                lado = double.Parse(Console.ReadLine());
                Console.WriteLine("Ingrese el segundo lado del triangulo....");
                lado2 = double.Parse(Console.ReadLine());

                resultado = (lado + lado2) + (Math.Sqrt((Math.Pow(lado, 2) + Math.Pow(lado2, 2))));
